changeSet.sql:
CREATE TABLE IF NOT EXISTS users ( id uuid NOT NULL PRIMARY KEY, username character varying(255), firstname character varying(255), lastname character varying(255), phone character varying(20), email character varying(255), created_timestamp bigint, social_media_link character varying(255) );
CREATE TABLE IF NOT EXISTS business_accounts ( id uuid NOT NULL PRIMARY KEY, name character varying(255) );
CREATE TABLE IF NOT EXISTS user_business_accounts ( business_account_id uuid NOT NULL, user_id uuid NOT NULL, FOREIGN KEY (business_account_id) REFERENCES business_accounts(id) ON DELETE CASCADE, FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE );
